import React from "react";
import confetti from 'react-canvas-confetti'
const Newsletter = (props) => {
  const handleClick = (e) => {
		e.preventDefault();
		confetti({
			particleCount: 150,
			spread: 60
		});
  }
  return (
    <div className="w-full py-16 text-white px-4 bg-black">
      <div className="max-w-[1240px] mx-auto grid lg:grid-cols-3">
        <div className="lg:col-span-2 my-4">
          <h1 className="md:text-5xl sm:text-3xl text-xl font-bold py-2 ">
            Want tips & tricks to optimize your flow?
          </h1>
          <p>Sign Up to our newsletter to stay up to date.</p>
        </div>
        <div className="my-4">
          <div className="flex flex-col sm:flex-row items-center justify-between w-full">
            <input
              className="p-3 flex w-full rounded-md text-black"
              type="email"
              placeholder="Enter Your Email"
            />
            <button onClick={handleClick}>
            className="bg-[#00df9a] w-[200px] rounded-md font-medium my-6 ml-4 px-6 py-3 text-black">
              Notify Me!
            </button>
          </div>
          <p>We care about the protection of your data.Read our <span className="text-[#00df9a] italic hover:underline">Privacy Policy</span></p>
        </div>
      </div>
    </div>
  );
};

export default Newsletter;
